import * as React from 'react';
import Box from '@mui/material/Box';
import { Button } from '@mui/material';
import List from '@mui/material/List';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import ExpandLess from '@mui/icons-material/ExpandLess';
import ExpandMore from '@mui/icons-material/ExpandMore';
import Collapse from '@mui/material/Collapse';

function login (id:string) {
    if(id!==""){
        return false
    }else{
        return true
    }
}
export default function SelectTextFields(props:any) {
    const [Files,setFiles] = React.useState({
        movies : [],
        times  : []
    });
    const [open, setOpen] = React.useState({
        time : false,
        movie : false
    });
    const handleClick1 = () => {
        console.log(open);
        setOpen({
            ...open,
            time : !open.time,
            movie : open.movie
        });
        console.log(open);
    };
    const handleClick2 = () => {
        setOpen({
            ...open,
            time : open.time,
            movie : !open.movie
        });
    };

    const requestOptionsFiles ={
        method: 'POST',
        headers:{'Content-Type': 'application/json'},
        body: JSON.stringify({})
    };
    return (
        <Box
            component="form"
            sx={{
                '& .MuiTextField-root': { m: 1, width: '25ch' },
            }}
            noValidate
            autoComplete="off"
        >
            <p>{props.message}</p>
            <Button
                variant="contained"
                disabled={login(props.id)}
                onClick={()=>{
                    let url = "http://localhost:8080/FILES?ID="+props.id
                    fetch(url,requestOptionsFiles)
                    .then((response)=> response.json())
                    .then((responseJson) =>{
                        console.log(responseJson);
                        setFiles({
                            ...Files,
                            movies:responseJson.time,
                            times:responseJson.movie
                        })
                    })
                }}
            >
                一覧取得
            </Button>
            <div>
                <ListItemButton onClick={handleClick1}>
                    <ListItemText primary="TimeStampFile" />
                    {open.time ? <ExpandLess /> : <ExpandMore />}
                </ListItemButton>
                    <Collapse in={open.time} timeout="auto" unmountOnExit>
                        <List component="div" disablePadding>
                            {Files.times.map((n)=>(
                                <ListItemText primary={n} />
                            ))}
                        </List>
                    </Collapse>
                <ListItemButton onClick={handleClick2}>
                    <ListItemText primary="MovieFile" />
                    {open.movie ? <ExpandLess /> : <ExpandMore />}
                </ListItemButton>
                    <Collapse in={open.movie} timeout="auto" unmountOnExit>
                        <List component="div" disablePadding>
                            {Files.movies.map((n)=>(
                                <ListItemText primary={n} />
                            ))}
                        </List>
                    </Collapse>
            </div>
        </Box>
    );
}
