import * as React from 'react';
import Box from '@mui/material/Box';
import { Button } from '@mui/material';
import List from '@mui/material/List';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import ExpandLess from '@mui/icons-material/ExpandLess';
import ExpandMore from '@mui/icons-material/ExpandMore';
import Collapse from '@mui/material/Collapse';
import {useDropzone} from 'react-dropzone';

function login (id:string) {
    if(id!==""){
        return false
    }else{
        return true
    }
}
export default function SelectTextFields(props:any) {
    const [Files,setFiles] = React.useState({
        movies : [],
        times  : []
    });
    const [open, setOpen] = React.useState({
        time : false,
        movie : false
    });
    const handleClick1 = () => {
        console.log(open);
        setOpen({
            ...open,
            time : !open.time,
            movie : open.movie
        });
        console.log(open);
    };
    const handleClick2 = () => {
        setOpen({
            ...open,
            time : open.time,
            movie : !open.movie
        });
    };
    var formData = new FormData();
    function onDrop(files:File[]) {
        var formData2 = new FormData();
        files.map(file=>{
            formData2.append(file.name,file)
            console.log(file.name);
        })
        formData = formData2
    }
    const requestOptionsFiles ={
        method: 'POST',
        headers:{'Content-Type': 'application/json'},
        body: formData
    };
    const {acceptedFiles, getRootProps, getInputProps} = useDropzone({onDrop});
    const files = acceptedFiles.map(file => (
        <li>{file.name}　{file.size}bytes</li>
    ));

    return (
        <Box
            component="form"
            sx={{
                '& .MuiTextField-root': { m: 1, width: '25ch' },
            }}
            noValidate
            autoComplete="off"
        >
            <p>{props.message}</p>
            <Button
                variant="contained"
                disabled={login(props.id)}
                onClick={()=>{
                    let url = "http://localhost:8080/FILES?ID="+props.id
                    fetch(url,requestOptionsFiles)
                    .then((response)=> response.json())
                    .then((responseJson) =>{
                        console.log(responseJson);
                        if(responseJson.movie == null && responseJson.time != null){
                            console.log(responseJson.movie);
                            setFiles({
                                ...Files,
                                times : responseJson.time,
                                movies : []
                            })
                        }else if(responseJson.movie != null && responseJson.time == null){
                            console.log(responseJson.time);
                            setFiles({
                                ...Files,
                                times : [],
                                movies : responseJson.movie
                            })
                        }else if(responseJson.movie != null && responseJson.time != null){
                            setFiles({
                                ...Files,
                                times : responseJson.time,
                                movies : responseJson.movie
                            })
                        }else{
                            setFiles({
                                ...Files,
                                times : [],
                                movies : []
                            })
                        }
                    })
                }}
            >
                一覧取得
            </Button>
            <div>
                <ListItemButton onClick={handleClick1}>
                    <ListItemText primary="TimeStampFile" />
                    {open.time ? <ExpandLess /> : <ExpandMore />}
                </ListItemButton>
                    <Collapse in={open.time} timeout="auto" unmountOnExit>
                        <List component="div" disablePadding>
                            {Files.times.map((n)=>(
                                <ListItemText primary={n} />
                            ))}
                        </List>
                    </Collapse>
                <ListItemButton onClick={handleClick2}>
                    <ListItemText primary="MovieFile" />
                    {open.movie ? <ExpandLess /> : <ExpandMore />}
                </ListItemButton>
                    <Collapse in={open.movie} timeout="auto" unmountOnExit>
                        <List component="div" disablePadding>
                            {Files.movies.map((n)=>(
                                <ListItemText primary={n} />
                            ))}
                        </List>
                    </Collapse>
            </div>
            <section className="container">
            <div {...getRootProps({className: 'dropzone'})}>
                <input {...getInputProps()} />
                <p>ドラッグドロップするかここを押してファイルを選択してください</p>
            </div>
            <aside>
                <h4>Files</h4>
                <ul>{files}</ul>
            </aside>
            <Button
                variant="contained"
                onClick={()=>{
                    var url = "http://localhost:8080/MOVIEUPLOAD?ID="+props.id
                    fetch(url,requestOptionsFiles)
                    .then((response)=> response.json())
                    .then((responseJson) =>{
                        console.log(responseJson);
                    })
                    .catch(e=>{
                        console.log(e);
                    })
            }}
            >
                送信
            </Button>
            </section>
        </Box>
    );
}
