import * as React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import MenuItem from '@mui/material/MenuItem';

var MovieFiles = ["movie1","movie2","movie3","movie4"];
var TimeFiles = ["time1","time2","time3","time4"];

var movies : any = [];
var times : any= [];

for(let m of MovieFiles){
    movies.push({
        value: m,
        label: m,
    })
}

for(let t of TimeFiles){
    times.push({
        value: t,
        label: t,
    })
}

export default function SelectTextFields() {
    const [files1, setFiles1] = React.useState('movie');
    const [files2, setFiles2] = React.useState('time');

    const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setFiles1(event.target.value);
        setFiles2(event.target.value);
    };

    return (
        <Box
            component="form"
            sx={{
                '& .MuiTextField-root': { m: 1, width: '25ch' },
            }}
            noValidate
            autoComplete="off"
        >
            <div>
                <TextField
                    id="outlined-select-currency"
                    select
                    label="your MovieFiles"
                    value={files1}
                    onChange={handleChange}
                    helperText=""
                >
                    {movies.map((option : any) => (
                        <MenuItem key={option.value} value={option.value}>
                        {option.label}
                        </MenuItem>
                    ))}
                </TextField>
                <TextField
                    id="outlined-select-currency"
                    select
                    label="your TimeFiles"
                    value={files2}
                    onChange={handleChange}
                    helperText=""
                >
                    {times.map((option : any) => (
                        <MenuItem key={option.value} value={option.value}>
                        {option.label}
                        </MenuItem>
                    ))}
                </TextField>
            </div>
        </Box>
    );
}
