import * as React from 'react';
import Button from '@mui/material/Button';
import Stack from '@mui/material/Stack';
import { FlagOutlined } from '@material-ui/icons';

export default function ContainedButtons() {
    const [recordState, setState] = React.useState({
        start: true,
        break: false,
        stamp: false,
        stop: false,
        save: false
    });
    return (
        <Stack direction="row" spacing={2}>
            <Button 
                variant="contained"
                onClick={()=>{
                    setState({
                        ...recordState,
                        start:false,
                        break: true,
                        stamp: true,
                        stop: true,
                        save: false
                    });
                }}
                {...recordState.start}
            >
                スタート
            </Button>
            <Button
                variant="contained"
                onClick={()=>{
                    setState({
                        ...recordState,
                        start: true,
                        break: false,
                        stamp: false,
                        stop: false,
                        save: false
                    });
                }}
                {...recordState.break}
            >
                一時中断
            </Button>
            <Button
                variant="contained"
                onClick={()=>{
                    setState({
                        ...recordState,
                        start: false,
                        break: true,
                        stamp: true,
                        stop: true,
                        save: false
                    });
                }}
                {...recordState.stamp}
            >
                タイムスタンプ
            </Button>
            <Button 
                variant="contained"
                onClick={()=>{
                    setState({
                        ...recordState,
                        start: false,
                        break: false,
                        stamp: false,
                        stop: false,
                        save: true
                    });
                }}
                {...recordState.stop}
            >
                停止
            </Button>
            <Button
                variant="contained"
                onClick={()=>{
                    setState({
                        ...recordState,
                        start: true,
                        break: false,
                        stamp: false,
                        stop: false,
                        save: false
                    });
                }}
                {...recordState.save}
            >
                保存
            </Button>
        </Stack>
    );
}
