import * as React from 'react';
import Button from '@mui/material/Button';
import Stack from '@mui/material/Stack';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import MenuItem from '@mui/material/MenuItem';


export default function OutPut() {
    const AllTimeFile: string[] = ["a","b","c"];
    const AllMovieFile: string[] = ["a",",b","c"];
    const tutorial = "開始時間\nキーワード：\n区間毎の冒頭\nの順に表示されます。\n"

    const index: string[] = [
        "00:00:00~\nキーワード：a,b,c,d,e\n今世紀はじめ~",
        "00:00:00~\nキーワード：a,b,c,d,e\n線形代数とはなにか〜",
        "00:00:00~\nキーワード：a,b,c,d,e\nでは続いて、対角化について〜",
        "00:00:00~\nキーワード：a,b,c,d,e\nいよいよ固有ベクトルをもちいて〜"
    ]
    /*const handleChange = (t : string[]) => {
        let IndexText = "";
        for(let T of t){
            IndexText+=T;
        }
        setIndex({
            ...Index,
            IndexT:IndexText
        })
    };*/
    const [indexText,setIndex] = React.useState({
        IndexT : ""
    });

    return (
        <Box
            sx={{
                '& .MuiTextField-root': { m: 1, width: '100ch' },
            }}
        >
            <Box
                component="form"
                sx={{
                    '& .MuiTextField-root': { m: 1, width: '25ch' },
                }}
                noValidate
                autoComplete="off"
            >
                <div>
                    <TextField
                        id="outlined-select-currency"
                        select
                        label="your MovieFiles"
                        value="TimeFile"
                        helperText=""
                    >
                        {AllTimeFile.map((value) => (
                            <MenuItem key={value} value={value}>
                                {value}
                            </MenuItem>
                        ))}
                    </TextField>
                    <TextField
                        id="outlined-select-currency"
                        select
                        label="your TimeFiles"
                        value="MovieFile"
                        helperText=""
                    >
                        {AllMovieFile.map((value) => (
                            <MenuItem key={value} value={value}>
                            {value}
                            </MenuItem>
                        ))}
                    </TextField>
                </div>
            </Box>
            <Stack direction="row" spacing={2}>
                <Button 
                    variant="contained"
                    onClick={()=>{
                        let IndexText = "";
                        for(let T of index){
                            IndexText+=T;
                        }
                        setIndex({
                            ...indexText,
                            IndexT:IndexText
                        })
                    }}
                >
                    作成
                </Button>
            </Stack>
            <TextField
                id="outlined-multiline-flexible"
                multiline
                fullWidth
                label="INDEX"
                value={indexText.IndexT}
            >
            </TextField>
        </Box>
    );
}
