import * as React from 'react';
import Button from '@mui/material/Button';
import Stack from '@mui/material/Stack';

export default function ContainedButtons() {
    const [recordState, setState] = React.useState({
        start: false,
        break: true,
        stamp: true,
        stop:  true,
        save:  true
    });
    return (
        <Stack direction="row" spacing={2}>
            <Button 
                variant="contained"
                onClick={()=>{
                    setState({
                        ...recordState,
                        start: true,
                        break: false,
                        stamp: false,
                        stop:  false,
                        save:  true
                    });
                }}
                disabled={recordState.start}
            >
                スタート
            </Button>
            <Button
                variant="contained"
                onClick={()=>{
                    setState({
                        ...recordState,
                        start: false,
                        break: true,
                        stamp: true,
                        stop:  true,
                        save:  true
                    });
                }}
                disabled={recordState.break}
            >
                一時中断
            </Button>
            <Button
                variant="contained"
                onClick={()=>{
                    setState({
                        ...recordState,
                        start: true,
                        break: false,
                        stamp: false,
                        stop:  false,
                        save:  true
                    });
                }}
                disabled={recordState.stamp}
            >
                タイムスタンプ
            </Button>
            <Button 
                variant="contained"
                onClick={()=>{
                    setState({
                        ...recordState,
                        start: true,
                        break: true,
                        stamp: true,
                        stop:  true,
                        save:  false
                    });
                }}
                disabled={recordState.stop}
            >
                停止
            </Button>
            <Button
                variant="contained"
                onClick={()=>{
                    setState({
                        ...recordState,
                        start: false,
                        break: true,
                        stamp: true,
                        stop:  true,
                        save:  true
                    });
                }}
                disabled={recordState.save}
            >
                保存
            </Button>
            <Button 
                variant="contained"
                onClick={()=>{
                    setState({
                        ...recordState,
                        start: false,
                        break: true,
                        stamp: true,
                        stop:  true,
                        save:  true
                    });
                }}
            >
                リセット※記録は破棄されます
            </Button>
        </Stack>
    );
}
