import * as React from 'react';
import Box from '@mui/material/Box';
import { Button } from '@mui/material';
import ListSubheader from '@mui/material/ListSubheader';
import List from '@mui/material/List';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import ExpandLess from '@mui/icons-material/ExpandLess';
import ExpandMore from '@mui/icons-material/ExpandMore';
import Collapse from '@mui/material/Collapse';
import ListItemIcon from '@mui/material/ListItemIcon';
import StarBorder from '@mui/icons-material/StarBorder';

const [open, setOpen] = React.useState({
    time : false,
    movie : false
});

const handleClick1 = () => {
    setOpen({
        ...open,
        time : !open.time,
        movie : open.movie
    });
};
const handleClick2 = () => {
    setOpen({
        ...open,
        time : open.time,
        movie : !open.movie
    });
};

let m : any = [];
let t : any = [];
const [Files,setFiles] = React.useState({
    movies : m,
    times  : t
});
function login (id:string) {
    if(id!==""){
        return false
    }else{
        return true
    }
}
export default function SelectTextFields(props:any) {
    const [files1, setFiles1] = React.useState('movie');
    const [files2, setFiles2] = React.useState('time');
    const handleChange1 = (event: React.ChangeEvent<HTMLInputElement>) => {
        setFiles1(event.target.value);
    };
    const handleChange2 = (event: React.ChangeEvent<HTMLInputElement>) => {
        setFiles2(event.target.value);
    };

    const requestOptionsFiles ={
        method: 'POST',
        headers:{'Content-Type': 'application/json'},
        body: JSON.stringify({})
    };
    return (
        <Box
            component="form"
            sx={{
                '& .MuiTextField-root': { m: 1, width: '25ch' },
            }}
            noValidate
            autoComplete="off"
        >
            <p>{props.message}</p>
            <Button
                variant="contained"
                disabled={login(props.id)}
                onClick={()=>{
                    let url = "http://localhost:8080/FILES?ID="+props.id
                    fetch(url,requestOptionsFiles)
                    .then((response)=> response.json())
                    .then((responseJson) =>{
                        var Movies  = [];
                        var Times   = [];
                        for(let m of responseJson.movie){
                            Movies.push({
                                value: m,
                                label: m,
                            })
                        }
                        for(let t of responseJson.time){
                            Times.push({
                                value: t,
                                label: t,
                            })
                        }
                        setFiles({
                            ...Files,
                            movies:Movies,
                            times:Times
                        })
                    })
                }}
            >
                一覧取得
            </Button>
            <div>
                <ListItemButton onClick={handleClick1}>
                    <ListItemText primary="Inbox" />
                        {open.time ? <ExpandLess /> : <ExpandMore />}
                </ListItemButton>
                <Collapse in={open.time} timeout="auto" unmountOnExit>
                    <List component="div" disablePadding>
                        <ListItemText primary="Starred" />
                    </List>
                </Collapse>
            </div>
        </Box>
    );
}
