import * as React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import MenuItem from '@mui/material/MenuItem';
import { Button } from '@mui/material';

const [Files,setFiles] = React.useState({
    movies : [""],
    times  : [""]
});
function login (id:string) {
    if(id!==""){
        return false
    }else{
        return true
    }
}
export default function SelectTextFields(props:any) {
    const [files1, setFiles1] = React.useState('movie');
    const [files2, setFiles2] = React.useState('time');
    const handleChange1 = (event: React.ChangeEvent<HTMLInputElement>) => {
        setFiles1(event.target.value);
    };
    const handleChange2 = (event: React.ChangeEvent<HTMLInputElement>) => {
        setFiles2(event.target.value);
    };

    const requestOptionsFiles ={
        method: 'POST',
        headers:{'Content-Type': 'application/json'},
        body: JSON.stringify({})
    };
    return (
        <Box
            component="form"
            sx={{
                '& .MuiTextField-root': { m: 1, width: '25ch' },
            }}
            noValidate
            autoComplete="off"
        >
            <p>{props.message}</p>
            <Button
                variant="contained"
                disabled={login(props.id)}
                onClick={()=>{
                    let url = "http://localhost:8080/FILES?ID="+props.id
                    fetch(url,requestOptionsFiles)
                    .then((response)=> response.json())
                    .then((responseJson) =>{
                        setFiles({
                            ...Files,
                            movies:responseJson.movies,
                            times:responseJson.times
                        })
                    })
                }}
            >
                一覧取得
            </Button>
            <div>
                <TextField
                    id="outlined-select-currency"
                    select
                    label="your MovieFiles"
                    value={files1}
                    onChange={handleChange1}
                    helperText=""
                >
                    {Files.movies.map((option : any) => (
                        <MenuItem key={option} value={option}>
                            {option}
                        </MenuItem>
                    ))}
                </TextField>
                <TextField
                    id="outlined-select-currency"
                    select
                    label="your TimeFiles"
                    value={files2}
                    onChange={handleChange2}
                    helperText=""
                >
                    {Files.times.map((option : any) => (
                        <MenuItem key={option} value={option}>
                            {option}
                        </MenuItem>
                    ))}
                </TextField>
            </div>
        </Box>
    );
}
