import * as React from 'react';
import Button from '@mui/material/Button';
import Stack from '@mui/material/Stack';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import MenuItem from '@mui/material/MenuItem';


export default function OutPut(props:any) {
    const URL = "http://localhost:8080"
    const tutorial = "開始時間\nキーワード：\n区間毎の冒頭\nの順に表示されます。\n\n"

    const index: string[] = [
        "00:00:00~\n解析区間外\n\n",
        "00:00:21~\nキーワード：マイナス * 行 席 数 成分 横 普通 かけ 今日\n線形代数煮物連続講義大猫男で行列を使ってきたいと思います上越の最初にこんなもんだって\n\n",
        "00:17:21~\n解析区間外\n\n",
        "00:17:46~\nキーワード：ベクトル * 一次 平面 二つ ケース 平行 組 場合 高校\n第4501次独立と11とかの考え方について扱ってきたいと思います今はね実はこの考え方自体は高校数学平面ベクトル空間メスの中に現れていたんだねこのを習った必ずかにかかわらずねあのね最初にまずその確認の意味を込めて二次元讃岐前から話を始めていきましょう\n\n",
        "00:31:15~\n解析区間外\n\n",
        "00:32:25~\nキーワード：マイナス 計算 * 行列式 因子 AI プラス −1 注目 部分\n行列式に中でも用意して迎えたやつになりますこのような展開方法サイファー3かける3よりも大きいサイズのね強烈な場合に宜しくは計算できるなので楽しみにしてくださいじゃ早速願いしたいから話ししていきましょうじゃあ因子展開このように一般的な話をする前に\n\n",
        "00:59:47~\n解析区間外\n\n",
        "01:00:05~\nキーワード：行列 独立 変換 計算 X 全部 固有値 一次 対角化 固有ベクトル\n天下第13項では誰か使ってもないして扱っていきましょうまず最初に書かの定義を一緒に見ていきましょうこんな風な感じで N 次正方行列 A に対し適当な正則行列 P が存在して正則行列って逆行列を持たなけれたことあったりすると\n\n"
    ]
    /*const handleChange = (t : string[]) => {
        let IndexText = "";
        for(let T of t){
            IndexText+=T;
        }
        setIndex({
            ...Index,
            IndexT:IndexText
        })
    };*/
    const [Files,setFiles] = React.useState({
        movies : [],
        times  : []
    });
    const [indexText,setIndex] = React.useState({
        IndexT : ""
    });
    const [selectFile,setFile] = React.useState({
        time : "",
        movie : "",
    });
    const handleChange1 = (event: React.ChangeEvent<HTMLInputElement>) => {
        setFile({
            ...selectFile,
            time:event.target.value
        });
    };
    const handleChange2 = (event: React.ChangeEvent<HTMLInputElement>) => {
        setFile({
            ...selectFile,
            movie:event.target.value
        });
    };
    function login (id:string) {
        if(id!==""){
            return false
        }else{
            return true
        }
    }
    var formData = new FormData
    const requestOptionsFiles ={
        method: 'POST',
        body: formData
    };
    return (
        <Box
            sx={{
                '& .MuiTextField-root': { m: 1, width: '70ch' },
            }}
        >
            <p>{props.message}</p>
            <Button
            variant="contained"
            disabled={login(props.id)}
            onClick={()=>{
                let url = "http://localhost:8080/FILES?ID="+props.id
                fetch(url,requestOptionsFiles)
                .then((response)=> response.json())
                .then((responseJson) =>{
                    console.log(responseJson);
                    if(responseJson.movie == null && responseJson.time != null){
                        console.log(responseJson.movie);
                        setFiles({
                            ...Files,
                            times : responseJson.time,
                            movies : []
                        })
                    }else if(responseJson.movie != null && responseJson.time == null){
                        console.log(responseJson.time);
                        setFiles({
                            ...Files,
                            times : [],
                            movies : responseJson.movie
                        })
                    }else if(responseJson.movie != null && responseJson.time != null){
                        setFiles({
                            ...Files,
                            times : responseJson.time,
                            movies : responseJson.movie
                        })
                    }else{
                        setFiles({
                            ...Files,
                            times : [],
                            movies : []
                        })
                    }
                })
            }}
            >
                ファイル取得
            </Button>
            <Box
                component="form"
                sx={{
                    '& .MuiTextField-root': { m: 1, width: '25ch' },
                }}
                noValidate
                autoComplete="off"
            >
                <div>
                    <TextField
                        id="outlined-select-currency"
                        select
                        label="your MovieFiles"
                        value={selectFile.time}
                        helperText=""
                        onChange={handleChange1}
                    >
                        {Files.times.map((value) => (
                            <MenuItem key={value} value={value}>
                                {value}
                            </MenuItem>
                        ))}
                    </TextField>
                    <TextField
                        id="outlined-select-currency"
                        select
                        label="your TimeFiles"
                        value={selectFile.movie}
                        helperText=""
                        onChange={handleChange2}
                    >
                        {Files.movies.map((value) => (
                            <MenuItem key={value} value={value}>
                                {value}
                            </MenuItem>
                        ))}
                    </TextField>
                </div>
            </Box>
            <Stack direction="row" spacing={2}>
                <Button 
                    variant="contained"
                    onClick={()=>{
                        const requestOptions ={
                            method: 'POST',
                            headers:{'Content-Type': 'application/json'},
                            body: JSON.stringify({
                                time: selectFile.time,
                                movie: selectFile.movie
                            })
                        };
                        fetch(URL+"/makeindex",requestOptions)
                        let IndexText = tutorial;
                        for(let T of index){
                            IndexText+=T;
                        }
                        setIndex({
                            ...indexText,
                            IndexT:IndexText
                        })
                    }}
                >
                    作成
                </Button>
            </Stack>
            <TextField
                id="outlined-multiline-flexible"
                multiline
                fullWidth
                label={tutorial}
                value={indexText.IndexT}
            >
            </TextField>
        </Box>
    );
}
