import * as React from 'react';
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import LoginFrom from './loginform';
import Files from './files';
import Record from './record';
import OutPut from './output';
import Button from '@mui/material/Button';


interface TabPanelProps {
    children?: React.ReactNode;
    index: number;
    value: number;
}

function TabPanel(props: TabPanelProps) {
    const { children, value, index, ...other } = props;

    return (
        <div
        role="tabpanel"
        hidden={value !== index}
        id={`vertical-tabpanel-${index}`}
        aria-labelledby={`vertical-tab-${index}`}
        {...other}
        >
        {value === index && (
            <Box sx={{ p: 3 }}>
            <Typography>{children}</Typography>
            </Box>
        )}
        </div>
    );
}

function a11yProps(index: number) {
    return {
        id: `vertical-tab-${index}`,
        'aria-controls': `vertical-tabpanel-${index}`,
    };
}

export default function VerticalTabs() {
    const [value, setValue] = React.useState(0);

    const handleChange = (event: React.SyntheticEvent, newValue: number) => {
        setValue(newValue);
    };

    const [Info,setInfo] = React.useState({
        id:"b",
        username : "",
        password : "",
    });

    function ChangeId(e : any) {
        console.log("id",e);
        setInfo({
            ...Info,
            id : e
        }) 
        console.log("id",Info.id) 
    }
    const ChangePass = (e : any) => {
        setInfo({
            ...Info,
            password : e
        })
    }

    const ChangeName = (e : any) => {
        setInfo({
            ...Info,
            username : e
        })
    }

    return (
        <Box
        sx={{ flexGrow: 1, bgcolor: 'background.paper', display: 'flex', height: 224 }}
        >
            <Button
                onClick={()=>{
                    setInfo({
                        ...Info,
                        password : "pass"
                    })
                }}
            >
                {Info.password}
            </Button>
            <Tabs
                orientation="vertical"
                variant="scrollable"
                value={value}
                onChange={handleChange}
                aria-label="Vertical tabs example"
                sx={{ borderRight: 1, borderColor: 'divider' }}
            >
                <Tab label="Sign In/Sign Up" {...a11yProps(0)} />
                <Tab label="Record" {...a11yProps(1)} />
                <Tab label="Files" {...a11yProps(2)} />
                <Tab label="OutPut" {...a11yProps(3)} />
            </Tabs>
            <TabPanel value={value} index={0}>
                <p>a{Info.id}a</p>
                <LoginFrom ChangeId={ChangeId}ChangePass={ChangePass}ChangeName={ChangeName}id={Info.id}username={Info.username}password={Info.password}/>
            </TabPanel>
            <TabPanel value={value} index={1}>
                <p>雑談、休憩など解析に含めない時間は一時停止を押してください。本題に戻る時スタートで再開できます。</p>
                <Record id={Info.id}/>
            </TabPanel>
            <TabPanel value={value} index={2}>
                <Files id={Info.id}/>
            </TabPanel>
            <TabPanel value={value} index={3}>
                <OutPut id={Info.id}/>
            </TabPanel>
        </Box>
    );
}
