import * as React from 'react';
import Button from '@mui/material/Button';
import Stack from '@mui/material/Stack';
import Box from '@mui/material/Box';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';

function toTime(t : number){
    let mod = t%3600;
    let h = (t-mod)/3600;
    mod = (t-h*3600)%60;
    let m = (t-h*3600-mod)/60;
    let s = (t-h*3600-m*60);
    let sh,sm,ss;
    if(h<10){
        sh = "0" + h.toString();
    }else{
        sh = h.toString();
    }
    if(m<10){
        sm = "0" + m.toString();
    }else{
        sm = m.toString();
    }
    if(s<10){
        ss = "0" + s.toFixed(0);
    }else{
        ss = s.toFixed(0);
    }
    let timeString = sh+":"+sm+":"+ss;
    return timeString;
}

export default function ContainedButtons() {
    const [recordState, setState] = React.useState({
        start: false,
        break: true,
        stamp: true,
        stop:  true,
        save:  true
    });
    const [date,setDate] = React.useState({
        t: new Date()
    });
    let timeArray: number[] = [];
    let flagArray: boolean[] = [];
    const [timestamp, setStamp] = React.useState({
        time : timeArray,
        flag : flagArray
    });

    return (
        <Box>
            <Stack direction="row" spacing={2}>
                <Button 
                    variant="contained"
                    onClick={()=>{
                        let copyList = [...timestamp.time];
                        let copyListF = [...timestamp.flag];
                        if(copyList.length === 0){
                            copyList.push(0);
                            copyListF.push(true);
                        }else{
                            let tl = timestamp.time.length;
                            let nt = new Date();
                            let ott = date.t.getTime();
                            let ntt = nt.getTime();
                            copyList.push((timestamp.time[tl-1] + (ntt-ott)/1000));
                            copyListF.push(true);
                        }
                        setState({
                            ...recordState,
                            start: true,
                            break: false,
                            stamp: false,
                            stop:  false,
                            save:  true
                        });
                        setStamp({
                            ...timestamp,
                            time : copyList,
                            flag : copyListF
                        })
                        setDate({
                            ...date,
                            t : new Date()
                        })
                    }}
                    disabled={recordState.start}
                >
                    スタート
                </Button>
                <Button
                    variant="contained"

                    onClick={()=>{
                        let copyList = [...timestamp.time];
                        let copyListF = [...timestamp.flag];
                        let tl = timestamp.time.length;
                        let nt = new Date();
                        let ott = date.t.getTime();
                        let ntt = nt.getTime();
                        copyList.push((timestamp.time[tl-1] + (ntt-ott)/1000));
                        copyListF.push(false);
                        setState({
                            ...recordState,
                            start: false,
                            break: true,
                            stamp: true,
                            stop:  true,
                            save:  true
                        });
                        setDate({
                            ...date,
                            t: new Date()
                        })
                        setStamp({
                            ...timestamp,
                            time : copyList,
                            flag : copyListF
                        })
                        setDate({
                            ...date,
                            t : new Date()
                        })
                    }}
                    disabled={recordState.break}
                >
                    一時中断
                </Button>
                <Button
                    variant="contained"
                    onClick={()=>{
                        let copyList = [...timestamp.time];
                        let copyListF = [...timestamp.flag];
                        let tl = timestamp.time.length;
                        let nt = new Date();
                        let ott = date.t.getTime();
                        let ntt = nt.getTime();
                        copyList.push((timestamp.time[tl-1] + (ntt-ott)/1000));
                        copyListF.push(true);
                        setState({
                            ...recordState,
                            start: true,
                            break: false,
                            stamp: false,
                            stop:  false,
                            save:  true
                        });
                        setStamp({
                            ...timestamp,
                            time : copyList,
                            flag : copyListF
                        })
                        setDate({
                            ...date,
                            t : new Date()
                        })
                    }}
                    disabled={recordState.stamp}
                >
                    タイムスタンプ
                </Button>
                <Button 
                    variant="contained"
                    onClick={()=>{
                        setState({
                            ...recordState,
                            start: true,
                            break: true,
                            stamp: true,
                            stop:  true,
                            save:  false
                        });
                    }}
                    disabled={recordState.stop}
                >
                    停止
                </Button>
                <Button
                    variant="contained"
                    onClick={()=>{
                        setState({
                            ...recordState,
                            start: false,
                            break: true,
                            stamp: true,
                            stop:  true,
                            save:  true
                        });
                    }}
                    disabled={recordState.save}
                >
                    保存
                </Button>
                <Button 
                    variant="contained"
                    onClick={()=>{
                        let copyList : any = [];
                        setState({
                            ...recordState,
                            start: false,
                            break: true,
                            stamp: true,
                            stop:  true,
                            save:  true
                        });
                        setStamp({
                            ...timestamp,
                            time : copyList
                        })
                    }}
                >
                    リセット※記録は破棄されます
                </Button>
            </Stack>
            <TableContainer component={Paper}>
                <Table sx={{ minWidth: 650 }} aria-label="simple table">
                    <TableHead>
                        <TableRow>
                            <TableCell>時刻</TableCell>
                            <TableCell >フラグ</TableCell>
                            <TableCell >区間時間</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {timestamp.time.map((item,index) => {
                            let interval = timestamp.time[index+1]-item;
                            if(timestamp.flag[index]){
                                return(
                                    <TableRow
                                        key={index}
                                        sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                                    >
                                        <TableCell component="th" scope="row">
                                            {toTime(item)}~
                                        </TableCell>
                                        <TableCell >解析区間</TableCell>
                                        <TableCell >{toTime(interval)}</TableCell>
                                    </TableRow>
                                )
                            }else{
                                return(
                                    <TableRow
                                        key={index}
                                        sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                                    >
                                        <TableCell component="th" scope="row">
                                            {toTime(item)}~
                                        </TableCell>
                                        <TableCell >中断区間</TableCell>
                                        <TableCell >{toTime(interval)}</TableCell>
                                    </TableRow>
                                )
                            }

                        })}
                    </TableBody>
                </Table>
            </TableContainer>
        </Box>
    );
}
